document.addEventListener("DOMContentLoaded",()=>{const e=document.documentElement;document.getElementById("theme-light").addEventListener("click",()=>{e.className="",localStorage.setItem("theme","light")});document.getElementById("theme-dark").addEventListener("click",()=>{e.className="theme-dark",localStorage.setItem("theme","dark")});document.getElementById("theme-contrast").addEventListener("click",()=>{e.className="theme-contrast",localStorage.setItem("theme","contrast")});const t=localStorage.getItem("theme");"dark"===t&&(e.className="theme-dark");"contrast"===t&&(e.className="theme-contrast")});
